mapper.select:

select

  distinct


from n_visitor_passport_record




  ${queryExpand}


  group by ${groupByClause}


  order by ${orderByClause}


  limit #{limit} offset #{offset}

  select * from (
	select a.id,a.type,c.`name` communityName,
	b.`name` doorName,a.open_time openTime,
	case when a.type = 1 then (select t.`name` from n_biz_user t where a.buser_id = t.id) when a.type = 2 then (
	select tt.name from n_visitors_statistics tt where a.visitor_id = tt.visitors_id limit 1
	) else '' end userName,
	case when a.type = 1 then (select t.`phone` from n_biz_user t where a.buser_id = t.id) when a.type = 2 then (
	select tt.`phone` from n_visitors_statistics tt where a.visitor_id = tt.visitors_id limit 1
	) else '' end userPhone
	from n_visitor_passport_record a
	left outer join n_door_control b on a.door_controll_id = b.id
	left outer join n_community c on a.community_id = c.id
	where 1=1

		and a.community_id = #{communityId}


		and a.open_time > #{startTime,jdbcType=TIMESTAMP}


		and a.open_time < #{endTime,jdbcType=TIMESTAMP}


		and a.community_id in 



					#{community,jdbcType=BIGINT}





		and a.type = #{type}

    ) t where 1=1

		and userName like #{name}


		and userPhone like #{phone}

  	select count(1) from (
	select a.id,a.type,c.`name` communityName,
	b.`name` doorName,a.open_time openTime,
	case when a.type = 1 then (select t.`name` from n_biz_user t where a.buser_id = t.id) when a.type = 2 then (
	select tt.name from n_visitors_statistics tt where a.visitor_id = tt.visitors_id limit 1
	) else '' end userName,
	case when a.type = 1 then (select t.`phone` from n_biz_user t where a.buser_id = t.id) when a.type = 2 then (
	select tt.`phone` from n_visitors_statistics tt where a.visitor_id = tt.visitors_id limit 1
	) else '' end userPhone
	from n_visitor_passport_record a
	left outer join n_door_control b on a.door_controll_id = b.id
	left outer join n_community c on a.community_id = c.id
	where 1=1

		and a.community_id = #{communityId}


		and a.open_time > #{startTime,jdbcType=TIMESTAMP}


		and a.open_time < #{endTime,jdbcType=TIMESTAMP}


		and a.community_id in 



					#{community,jdbcType=BIGINT}





		and a.type = #{type}

    ) t where 1=1

		and userName like #{name}


		and userPhone like #{phone}
